import Vue from 'vue';
import Router from 'vue-router';
import HelloContainer from "./components/HelloWorld.vue"; //配置组件访问路由
//1.引入自定义组件

import HomeContainer from "./components/tabbar/HomeContainer";
import NewsContainer from "./components/news/NewsContainer";
import MemberContainer from "./components/tabbar/MemberContainer";
import ShopcarContainer from "./components/tabbar/ShopcarContainer";
import SearchContainer from "./components/tabbar/SearchContainer";
import NewsDetailContainer from "./components/news/NewsDetailContainer";
import GoodList from "./components/goods/GoodList";
import Goodsinfo from "./components/goods/Goodsinfo";
Vue.use(Router); //2.指定该组件的访问路径

export default new Router({
  routes: [{
    path: '/',
    redirect: "/home"
  }, {
    path: '/home',
    component: HomeContainer
  }, {
    path: "/home/newslist",
    component: NewsContainer
  }, {
    path: "/newsdetail/:id",
    component: NewsDetailContainer
  }, {
    path: '/member',
    component: MemberContainer
  }, {
    path: '/shopcar',
    component: ShopcarContainer
  }, {
    path: '/search',
    component: SearchContainer
  }, {
    path: '/home/goodlist',
    component: GoodList
  }, {
    path: "/home/goodsinfo/:id",
    component: Goodsinfo,
    name: "goodsinfo"
  }],
  linkActiveClass: "mui-active" //覆盖默认路由高亮class

});
